'use strict';

// @todo
// provide error handler interface
// @todo
// redirect to error page when uncaught error occured
// 页面 didMount 之前的错误无法 didMount..
if (process && process.env && process.env.NODE_ENV === 'development') {
  global.ErrorUtils && global.ErrorUtils.setGlobalHandler(function (e) {
    console.error(e);
  });
}